import { deleteUser, listUserByPage } from '@/services/userService';
import {
  ActionType,
  PageContainer,
  ProColumns,
  ProTable,
} from '@ant-design/pro-components';
import {  Divider, message, Popconfirm, Space, Typography,Image} from 'antd';
import React, { useRef, useState } from 'react';
import UpdateModal from './components/UpdateModal';

/**
 *  删除节点
 * @param selectedRows
 */
const doDelete = async (selectedRows: UserType.User[]) => {
  const hide = message.loading('正在删除');
  if (!selectedRows) return true;
  try {
    await deleteUser({
      id: selectedRows.find((row) => row.id)?.id || 0,
    });
    message.success('操作成功');
  } catch (e: any) {
    message.error('操作失败，' + e.message);
  } finally {
    hide();
  }
};

/**
 * 用户管理页面
 * @constructor
 */
const AdminUserPage: React.FC<unknown> = () => {
  const [updateModalVisible, setUpdateModalVisible] = useState<boolean>(false);
  const [updateData, setUpdateData] = useState<UserType.User>({});
  const actionRef = useRef<ActionType>();

  /**
   * 表格列配置
   */
  const columns: ProColumns<UserType.User>[] = [
    {
      title: '序号',
      dataIndex: 'id',
      // valueType: 'index',
      valueType: 'indexBorder',
    },
    {
      title: '用户昵称',
      dataIndex: 'userName',
      valueType: 'text',
    },
    {
      title: '账号',
      dataIndex: 'userAccount',
      valueType: 'text',
      copyable: true,  //是否可复制
    },
    {
      title: '头像',
      dataIndex: 'userAvatar',
      render: (_, record) => (
          <div>
            <Image
                width={100}
                height={100}
                src={record.userAvatar|| "https://img2.baidu.com/it/u=3022488819,1069187648&fm=253&fmt=auto&app=138&f=JPG?w=500&h=500"} // 设置默认头像URL
                fallback="data:image/png;base64,"
            />

          </div>
      ),
      search: false, // 关闭查询功能

    },


    {
      title: '性别',
      dataIndex: 'gender',
      valueEnum:{
        0:{text: '未知'},
        1:{text: '男'},
        2:{text: '女'},
      },
    },

    {
      title: '用户角色',
      dataIndex: 'userRole',
      valueEnum:{
        user:{text: '普通用户',
          status:'Default'
        },

        admin:{text: '管理员',
          status: 'Success',
        },
      },
    },
    {
      title: '创建时间',
      dataIndex: 'createTime',
      valueType: 'dateTime',
      hideInForm: true,
      search: false, // 关闭查询功能
    },
    {
      title: '更新时间',
      dataIndex: 'updateTime',
      valueType: 'dateTime',
      hideInForm: true,
      search: false, // 关闭查询功能
    },
    {
      title: '操作',
      dataIndex: 'option',
      valueType: 'option',
      render: (_, record) => (
        <Space split={<Divider type="vertical" />}>
          <Typography.Link
            onClick={() => {
              setUpdateData(record);
              setUpdateModalVisible(true);
            }}
          >
            修改
          </Typography.Link>
          <Popconfirm
            title="您确定要删除么？"
            onConfirm={() => doDelete([record])}
            okText="确认"
            cancelText="取消"
          >
            <Typography.Link type="danger">删除</Typography.Link>
          </Popconfirm>
        </Space>
      ),
    },
  ];

  return (
    <PageContainer>

      <ProTable<UserType.User>
        headerTitle="用户管理"
        actionRef={actionRef}
        rowKey="id"
        // rowKey={(record) => `user_${record.id}`}
        search={{
          labelWidth: 'auto',
        }}

        request={async (params, sorter, filter) => {
          const { data, code } = await listUserByPage({
            ...params,
            // @ts-ignore
            sorter,
            filter,
          });
          return {
            data: data?.records || [],
            success: code === 0,
            total: data.total,
          } as any;
        }}
        columns={columns}
      />

      <UpdateModal
        oldData={updateData}
        modalVisible={updateModalVisible}
        columns={columns}
        onSubmit={() => {}}
        onCancel={() => setUpdateModalVisible(false)}
      />
    </PageContainer>
  );
};

export default AdminUserPage;
